parameters.jsCode:
const msg = $input.first().json.body.data.quote.msg || '';

// Regex lấy ID YouTube Shorts
const ytMatch = msg.match(/https:\/\/www\.youtube\.com\/shorts\/([\w-]+)/);

// Regex lấy ID Facebook
const fbMatch = msg.match(/Video id fb:\s*(\d+)/);

return [
  {
    json: {
      youtubeId: ytMatch ? ytMatch[1] : null,
      fbVideoId: fbMatch ? fbMatch[1] : null
    }
  }
];

// Lấy post_id từ input
const postId = $json.post_id;

// Tạo link Reel
const reelLink = `https://www.facebook.com/${postId}`;

// Trả kết quả
return [
  {
    json: {
      post_id: postId,
      reel_link: reelLink
    }
  }
];
